import{_ as i,c as a,a3 as r,o as t}from"./chunks/framework.uL7cFZ_M.js";const p=JSON.parse('{"title":"Homemade Ginger Ale","description":"","frontmatter":{"tags":["recipe","#drinks/clean"],"created":"2005-12-06T00:43:58.000Z","author":null,"source":"https://www.simplyrecipes.com/recipes/homemade_ginger_ale/","title":"Homemade Ginger Ale","type":"Recipe","topic":["Drinks"],"outline":"deep"},"headers":[],"relativePath":"recipes/drinks/ginger-ale.md","filePath":"recipes/drinks/ginger-ale.md","lastUpdated":null}'),l={name:"recipes/drinks/ginger-ale.md"};function n(s,e,o,d,c,m){return t(),a("div",null,e[0]||(e[0]=[r('<h1 id="homemade-ginger-ale" tabindex="-1">Homemade Ginger Ale <a class="header-anchor" href="#homemade-ginger-ale" aria-label="Permalink to &quot;Homemade Ginger Ale&quot;">​</a></h1><p>Ginger ale is so easy to make at home, you might just stop buying it from the store. All you need is grated ginger, lime, and sugar, you can make this refreshing homemade ginger ale in no time.</p><p>CookTime: 30m TotalTime: 35m</p><p><img src="https://www.simplyrecipes.com/thmb/q5LEIII9iHuGoq3zPjdT_IXpQkI=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/Simply-Recipes-Ginger-Ale-LEAD-4-d7bb96ceb994481ab4072f76c0cc1291.jpg" alt="Homemade Ginger Ale"></p><h3 id="ingredients" tabindex="-1">Ingredients <a class="header-anchor" href="#ingredients" aria-label="Permalink to &quot;Ingredients&quot;">​</a></h3><ul><li>Ginger Water</li><li>1 cup peeled, finely chopped ginger</li><li>2 cups water</li><li>Simple Syrup</li><li>1 cup sugar</li><li>1 cup water</li><li>To Serve</li><li>Club soda</li><li>Ice, optional</li><li>Lime juice</li><li>Lime wedges</li></ul><h3 id="instructions" tabindex="-1">Instructions <a class="header-anchor" href="#instructions" aria-label="Permalink to &quot;Instructions&quot;">​</a></h3><ul><li>Infuse the water with ginger: Bring 2 cups of water to a boil in a saucepan. Add ginger. Reduce heat to medium low and let ginger sit in the simmering water for 5 minutes. Remove from heat and let sit for 20 minutes. Strain liquid through a fine mesh strainer. Discard ginger pieces.</li><li>Make the simple syrup: In a separate saucepan, make the simple syrup by dissolving 1 cup granulated sugar into 1 cup of boiling water. Set aside.</li><li>Assemble the ginger ale: Make individual (tall) glasses of ginger ale by mixing 1/2 cup of ginger water with 1/3 cup of Simple Syrup and 1/2 cup of club soda. Serve over ice, if you like. Add a few drops of fresh lime juice and a lime wedge to each glass. Did you enjoy this recipe? Let us know with a rating and review!</li></ul><hr><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2>',10)]))}const u=i(l,[["render",n]]);export{p as __pageData,u as default};
