import{_ as t,c as a,a3 as i,o as r}from"./chunks/framework.uL7cFZ_M.js";const u=JSON.parse('{"title":"Hard Apple Cider","description":"","frontmatter":{"title":"Hard Apple Cider","source":"https://www.growforagecookferment.com/how-to-make-hard-cider-part-1-brew-it/","author":"Colleen Codekas","type":"Recipe","tags":["drinks/alcohol"],"dateCreated":"2024-09-21 08:00","status":"Archive","outline":"deep"},"headers":[],"relativePath":"recipes/drinks/hard-cider.md","filePath":"recipes/drinks/hard-cider.md","lastUpdated":null}'),o={name:"recipes/drinks/hard-cider.md"};function l(n,e,h,s,d,c){return r(),a("div",null,e[0]||(e[0]=[i('<h1 id="hard-apple-cider" tabindex="-1"><a href="https://www.growforagecookferment.com/how-to-make-hard-cider-part-1-brew-it/" target="_blank" rel="noreferrer">Hard Apple Cider</a> <a class="header-anchor" href="#hard-apple-cider" aria-label="Permalink to &quot;[Hard Apple Cider](https://www.growforagecookferment.com/how-to-make-hard-cider-part-1-brew-it/)&quot;">​</a></h1><p>Learn how to brew your own easy and delicious hard cider with this simple recipe! Brewing your own hard cider is easy, affordable, and it tastes amazing.</p><p><img src="https://www.growforagecookferment.com/wp-content/uploads/2020/09/hard-cider-fb.jpg" alt="Hard Apple Cider"></p><h3 id="ingredients" tabindex="-1">Ingredients <a class="header-anchor" href="#ingredients" aria-label="Permalink to &quot;Ingredients&quot;">​</a></h3><ul><li>1 gallon organic apple juice (without preservatives)</li><li>1 pound brown sugar</li><li>1 cinnamon stick</li><li>3 whole cloves</li><li>10 raisins</li><li>1/2 package champagne yeast (or cote des blanc yeast)</li></ul><h3 id="instructions" tabindex="-1">Instructions <a class="header-anchor" href="#instructions" aria-label="Permalink to &quot;Instructions&quot;">​</a></h3><ul><li>Sanitize everything that will come into contact with your cider.</li><li>Heat ½ gallon of apple juice in a pot on medium heat, do not boil.</li><li>Add the brown sugar and stir to dissolve, then remove the pot from the heat.</li><li>Put the cinnamon stick, cloves, and raisins into the empty one gallon brewing jug.</li><li>Use the funnel to transfer the warm cider from the pot into the one gallon brewing jug. Allow the warm cider to steep with the spices for about 20 minutes.</li><li>Top off the jug with the remaining ½ gallon of apple juice, leaving 2 inches of headspace at the top of the jug. You will have a bit leftover apple juice.</li><li>Cap the jug and shake to combine everything.</li><li>Use a thermometer to check the temperature of the juice in the jug. When it has cooled to 90°F or less, you can add ½ package of yeast to the jug (doesn’t have to be exact). Store the opened package with the remaining yeast in the refrigerator for later use.</li><li>Cap the jug again and shake vigorously for about a minute or two to aerate the yeast.</li><li>Attach the airlock to the rubber stopper if you haven’t already, fill it with water to the fill line, then place it firmly on the top of the jug.</li><li>Put the jug in a cool (not cold) place out of direct sunlight to ferment. After several hours, or overnight, you will start to see bubbles forming in the jug and airlock.</li><li>Allow the cider to ferment for 3-4 weeks, until the bubbling in the jug and airlock has stopped.</li><li>Bottle the hard cider.</li></ul><hr><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2>',9)]))}const g=t(o,[["render",l]]);export{u as __pageData,g as default};
