import{_ as a,c as t,a3 as o,o as r}from"./chunks/framework.uL7cFZ_M.js";const d=JSON.parse('{"title":"Spicy-Sweet Buffalo Popcorn","description":"","frontmatter":{"title":"Spicy-Sweet Buffalo Popcorn","type":"Recipe","source":"https://www.allrecipes.com/recipe/261199/spicy-sweet-buffalo-popcorn/","topic":["Food"]},"headers":[],"relativePath":"recipes/food/buffalo-popcorn.md","filePath":"recipes/food/buffalo-popcorn.md","lastUpdated":null}'),i={name:"recipes/food/buffalo-popcorn.md"};function l(n,e,p,s,c,h){return r(),t("div",null,e[0]||(e[0]=[o('<h1 id="spicy-sweet-buffalo-popcorn" tabindex="-1">Spicy-Sweet Buffalo Popcorn <a class="header-anchor" href="#spicy-sweet-buffalo-popcorn" aria-label="Permalink to &quot;Spicy-Sweet Buffalo Popcorn&quot;">​</a></h1><h1 id="ingredients" tabindex="-1">Ingredients <a class="header-anchor" href="#ingredients" aria-label="Permalink to &quot;Ingredients&quot;">​</a></h1><ul><li>Original recipe yields 8 servings</li><li>Ingredient Checklist <ul><li>cooking spray</li><li>8 cups popped popcorn</li><li>1 cup white sugar</li><li>¼ cup water</li><li>⅓ cup Buffalo wing sauce (such as Frank&#39;s®)</li><li>3 tablespoons butter, cut into small cubes</li><li>½ teaspoon baking soda</li><li>¼ teaspoon cayenne pepper (Optional)</li></ul></li></ul><h1 id="directions" tabindex="-1">Directions <a class="header-anchor" href="#directions" aria-label="Permalink to &quot;Directions&quot;">​</a></h1><h2 id="step-1" tabindex="-1">Step 1 <a class="header-anchor" href="#step-1" aria-label="Permalink to &quot;Step 1&quot;">​</a></h2><p>Spray a large bowl with cooking spray. Fill with popcorn.</p><h2 id="step-2" tabindex="-1">Step 2 <a class="header-anchor" href="#step-2" aria-label="Permalink to &quot;Step 2&quot;">​</a></h2><p>Preheat oven to 300 degrees F (150 degrees C). Line a baking sheet with parchment paper or a silicone pad.</p><h2 id="step-3" tabindex="-1">Step 3 <a class="header-anchor" href="#step-3" aria-label="Permalink to &quot;Step 3&quot;">​</a></h2><p>Combine sugar and water in a saucepan and bring to a boil over medium-high heat. Cook, without stirring and swirling pan gently as needed, until sugar has melted and the caramel is a deep amber color, 8 to 12 minutes. Remove from heat.</p><h2 id="step-4" tabindex="-1">Step 4 <a class="header-anchor" href="#step-4" aria-label="Permalink to &quot;Step 4&quot;">​</a></h2><p>Stir Buffalo sauce and butter carefully into the caramel; it will spit and bubble and may form a ball. Return to heat; bring to a boil, stirring until caramel is dissolved, about 3 minutes. Remove from heat and stir in the baking soda and cayenne pepper.</p><h2 id="step-5" tabindex="-1">Step 5 <a class="header-anchor" href="#step-5" aria-label="Permalink to &quot;Step 5&quot;">​</a></h2><p>Pour spicy caramel mixture carefully over the popcorn. Toss to completely coat, guarding your hands from the hot caramel. Spread the coated popcorn onto the prepared baking sheet.</p><h2 id="step-6" tabindex="-1">Step 6 <a class="header-anchor" href="#step-6" aria-label="Permalink to &quot;Step 6&quot;">​</a></h2><p>Bake in the preheated oven until dry, 15 to 20 minutes. Let cool for 10 minutes; stir.</p><h1 id="source" tabindex="-1">Source <a class="header-anchor" href="#source" aria-label="Permalink to &quot;Source&quot;">​</a></h1><ul><li><a href="https://www.allrecipes.com/recipe/261199/spicy-sweet-buffalo-popcorn/" target="_blank" rel="noreferrer">https://www.allrecipes.com/recipe/261199/spicy-sweet-buffalo-popcorn/</a></li></ul>',18)]))}const f=a(i,[["render",l]]);export{d as __pageData,f as default};
